mapper.select:
SELECT COUNT(*)
FROM user_tb
WHERE user_id = #{userId}

mapper.insert:
INSERT INTO user_tb (
    user_id, name, password, phone_number, gender, birth
) VALUES (
    #{userId}, #{name}, #{password}, #{phoneNumber}, #{gender}, #{birth}
)

mapper.select:
SELECT *
FROM user_tb
WHERE user_id = #{userId} 
AND password = #{password}
